import styled from "styled-components/macro";

export const Banner = styled.section`
  position: relative;
  min-height: 70vh;
  display: grid;
  place-items: center;
  background: ${({ source }) =>
    source
      ? `url(${source}) center/cover no-repeat`
      : `linear-gradient(135deg, #e6f0ff 0%, #ffffff 100%)`};

  /* overlay for readability */
  &::before {
    content: "";
    position: absolute;
    inset: 0;
    background: linear-gradient(
      to bottom right,
      rgba(0, 0, 0, .40),
      rgba(0, 0, 0, .25)
    );
    pointer-events: none;
  }

  /* inner content wrapper */
  > .hero-inner {
    position: relative;
    z-index: 1;
    width: min(1120px, 92%);
    display: grid;
    grid-template-columns: 1.2fr 1fr;  /* text | image/empty */
    gap: 32px;
    align-items: center;
  }

  @media (max-width: 900px) {
    min-height: 58vh;
    > .hero-inner {
      grid-template-columns: 1fr;
      text-align: left;
    }
  }
`;
