import styled from "styled-components/macro";

export const Container = styled.div`
  width: 85%;
  margin: 0px auto;
  display: flex;
  justify-content: space-between;
  align-items: center;
  height: 100%;
  @media only screen and (max-width: 768px) {
    flex-direction: column;
  }
`;

export const Left = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;
  padding: 20px;
  width: 65%;
  @media only screen and (max-width: 768px) {
    width: 100%;
    margin-top: 60px;
  }
`;

export const Right = styled.div`
  background-color: rgba(231, 238, 241, 0.39);
  height: 100%;
  padding: 10px;
  margin: 60px 0px;
  width: 30%;
  position: relative;
  @media only screen and (max-width: 768px) {
    width: 70%;
  }
  @media only screen and (max-width: 600px) {
    width: 90%;
  }
`;
export const Title = styled.h1`
  font-size: clamp(2rem, 1.6rem + 2vw, 3.2rem);
  font-weight: 800;
  line-height: 1.2;
  color: rgba(0, 0, 0, 0.9); /* DARK text for clarity */
  text-shadow: 0 2px 4px rgba(255,255,255,0.5); /* optional subtle contrast */
  margin-bottom: 12px;
`;

export const Text = styled.p`
  font-size: clamp(1rem, .95rem + .3vw, 1.125rem);
  line-height: 1.5;
  color: rgba(0, 0, 0, 0.8); /* darker than current */
  max-width: 600px;
  margin: 0 auto;
`;
