export const Header = styled.div`
  width: 100%;
  z-index: 99;
  position: sticky;
  top: 0;

  /* transparent when bg === "true" and not fixed */
  ${({ bg }) => bg === "true" && `background: transparent;`}

  /* solid on normal pages */
  ${({ bg }) => bg !== "true" && `background-color: var(--bs-white);`}

  ${({ fixed }) =>
    fixed &&
    `
      background-color: var(--bs-white);
      position: fixed;
      top: 0;
      box-shadow: 0 8px 24px rgba(0,0,0,.08);
      backdrop-filter: saturate(180%) blur(8px);
      min-height: 80px;
      left: 0;
      animation: header 0.4s ease;
    `}

  @keyframes header {
    from { transform: translateY(-8px); opacity: .85; }
    to   { transform: translateY(0);    opacity: 1;   }
  }

  @media (max-width: 600px) {
    min-height: 72px;
    display: flex;
    align-items: center;
  }
`;
