import styled from "styled-components/macro";
import { Link } from "react-router-dom";

export const Listing = styled.div`
  background: #fff;
  border-radius: 12px;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  box-shadow: 0 8px 20px rgba(0,0,0,.06);
  transition: transform .18s ease, box-shadow .18s ease;

  &:hover {
    transform: translateY(-4px);
    box-shadow: 0 12px 28px rgba(0,0,0,.10);
  }
`;

/* --- TOP / IMAGE --- */
Listing.Top = styled.div`
  position: relative;
`;

Listing.TopItem = styled.div`
  aspect-ratio: 16 / 10;           /* keeps a consistent image height */
  width: 100%;
  overflow: hidden;
`;

Listing.Image = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;                /* fills the frame nicely */
  display: block;
`;

Listing.TopItemContainer = styled.div`
  position: absolute;
  inset: 0;
`;

Listing.TopItemInfo = styled.div`
  position: absolute;
  top: 10px;
  left: 10px;
  background: rgba(0,0,0,.65);
  color: #fff;
  padding: 6px 10px;
  border-radius: 9999px;
  font-size: .8rem;
  display: flex;
  align-items: center;
  gap: 6px;
`;

/* --- BOTTOM --- */
Listing.Bottom = styled.div`
  padding: 12px 14px 14px;
  display: flex;
  flex-direction: column;
  gap: 8px;
`;

Listing.BottomItem = styled.div`
  display: flex;
  flex-direction: column;
  gap: 8px;
`;

Listing.Title = styled.h3`
  margin: 4px 0 0;
  font-size: 1.05rem;
  line-height: 1.35;
  color: #111;

  a {
    color: inherit;
    text-decoration: none;
  }
  a:hover {
    text-decoration: underline;
  }
`;

Listing.Price = styled.div`
  color: var(--bs-blue);
  font-weight: 700;
`;

Listing.Text = styled.p`
  margin: 0;
  color: #6b7280;
  line-height: 1.55;

  ${({ description }) =>
    description &&
    `
      font-size: .9rem;
      max-width: 60ch;
    `}
`;

Listing.Button = styled.div`
  margin-top: 6px;

  a {
    display: inline-block;
    padding: 8px 12px;
    background: var(--bs-blue);
    color: #fff;
    border-radius: 8px;
    font-weight: 600;
    text-decoration: none;
  }

  a:hover {
    filter: brightness(.95);
  }
`;

/* --- AGENT --- */
Listing.AgentContainer = styled.div`
  margin-top: auto;  /* push to bottom */
  display: flex;
  align-items: center;
  gap: 10px;
  padding-top: 6px;
`;

Listing.AgentImageContainer = styled.div`
  width: 28px;
  height: 28px;
  overflow: hidden;
  border-radius: 50%;
`;

Listing.Icon = styled.i``;
Listing.Anchor = styled(Link)``;
