import React, { useEffect, useState, useCallback } from "react";
import { useLocation } from "react-router-dom";
import links from "../constants/routes/nav-links";
import { HeaderWrapper, Banner, Jumbotron } from "../components";
import { logout } from "../redux/actions/authActions"; // ✅ import logout action
import SideNavigationContainer from "./SideNavigationContainer";


const HeaderContainer = ({ bg, source }) => {
  const [isSideOpen, setIsSideOpen] = useState(false);
  const [fixed, setFixed] = useState(false);

///this is to get the user from the login page that i store there then pass it to index wherer i have the syntax of the btn 
   const [user, setUser] = useState(() => {
    try {
      return JSON.parse(localStorage.getItem("user")) || null;
    } catch {
      return null;
    }
  });
  const location = useLocation();

  // keep user in sync if it changes in another tab or after logout
  useEffect(() => {
    const onStorage = (e) => {
      if (e.key === "user") {
        try {
          setUser(e.newValue ? JSON.parse(e.newValue) : null);
        } catch {
          setUser(null);
        }
      }
    };
    window.addEventListener("storage", onStorage);
    return () => window.removeEventListener("storage", onStorage);
  }, []);



  // Handle sticky/fixed header safely
  useEffect(() => {
    const onScroll = () => setFixed(window.scrollY > 80);
    onScroll(); // run once on mount
    window.addEventListener("scroll", onScroll, { passive: true });
    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  // Close side nav on route change
  useEffect(() => {
    setIsSideOpen(false);
  }, [location.pathname]);

  const toggleSide = useCallback(() => setIsSideOpen(v => !v), []);


   // ✅ Logout handler
  const handleLogout = async () => {
    await dispatch(logout());
    localStorage.removeItem("user");
    setUser(null);
    history.push("/login");
  };

  return (
    <Banner bg={bg} source={source}>
      <HeaderWrapper bg={bg} fixed={false} role="navigation" aria-label="Primary">
        <HeaderWrapper.Container>
          <HeaderWrapper.Title bg={bg}>
            <HeaderWrapper.Link bg={bg} fixed={fixed} to="/">
              Real Home
            </HeaderWrapper.Link>
          </HeaderWrapper.Title>

          <HeaderWrapper.LinksContainer>
            {/* Main nav: hide Login when user is logged in */}
            <HeaderWrapper.List links="links" role="menubar">
              {links
                .filter(link => !(user && (link.to === "/login" || /login/i.test(link.name))))
                .map(link => (
                  <HeaderWrapper.Item key={link.to} role="none">
                    <HeaderWrapper.Anchor
                      role="menuitem"
                      bg={bg}
                      fixed={fixed}
                      to={link.to}
                    >
                      {link.name}
                    </HeaderWrapper.Anchor>
                  </HeaderWrapper.Item>
                ))}
            </HeaderWrapper.List>

            {/* Right side: show user's name as plain text OR Login link when logged out */}
            <HeaderWrapper.List>
              <HeaderWrapper.Item>
                {user ? (
                  // styled-components supports "as" to change the rendered tag
                 <HeaderWrapper.Anchor to="/profile" special="true">

                    {user.email }
                  </HeaderWrapper.Anchor>
                ) : (
                  <HeaderWrapper.Anchor to="/login" special="true">
                    Login  



                                      </HeaderWrapper.Anchor>
                )}
              </HeaderWrapper.Item>
            </HeaderWrapper.List>

            {/* Burger menu stays unchanged */}
            <HeaderWrapper.List side="side">
              <HeaderWrapper.Item>
                <HeaderWrapper.Button
                  aria-label="Open menu"
                  aria-controls="mobile-menu"
                  aria-expanded={isSideOpen}
                  onClick={toggleSide}
                >
                  <HeaderWrapper.Icon name="fa fa-bars" aria-hidden="true" />
                </HeaderWrapper.Button>
              </HeaderWrapper.Item>
            </HeaderWrapper.List>
          </HeaderWrapper.LinksContainer>
        </HeaderWrapper.Container>
      </HeaderWrapper>

      {bg === "true" && (
        <Jumbotron>
          <Jumbotron.Left>
            <Jumbotron.Title>Find the home you deserve</Jumbotron.Title>
            <Jumbotron.Text>
              Browse curated listings and contact trusted agents.
            </Jumbotron.Text>
          </Jumbotron.Left>
          <Jumbotron.Right />
        </Jumbotron>
      )}

      {isSideOpen && <div className="overlay" onClick={toggleSide} aria-hidden="true" />}

      <SideNavigationContainer
        sideNavShown={isSideOpen}
        sideNavHidden={!isSideOpen}
        setSideNavHidden={(v) => setIsSideOpen(!v)}
        setSideNavShown={setIsSideOpen}
      />
    </Banner>
  );
};


export default HeaderContainer;
