// src/pages/Listing.jsx
import React, { useEffect } from "react";
import styled from "styled-components/macro";
import { useDispatch, useSelector } from "react-redux";
import {
  HeaderContainer,
  ListingItemContainer,
  FooterContainer,
} from "../containers";
import { Section } from "../components";
import { getPropertyList } from "../redux/actions/propertiesAction";

/* Centered page wrapper + card grid */
const CenterWrap = styled.div`
  width: min(1200px, 92%);
  margin: 0 auto;             /* <-- centers the content */
`;

const Cards = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(320px, 1fr));
  gap: 24px;                  /* spacing between cards */
`;

const Empty = styled.p`
  width: 100%;
  text-align: center;
  color: #6b7280;
  padding: 40px 0;
  margin: 0;
`;

const Listing = () => {
  const dispatch = useDispatch();
  const { properties = [] } = useSelector((s) => s.propertyList || {});

  useEffect(() => {
    dispatch(getPropertyList());
  }, [dispatch]);

  return (
    <>
      <HeaderContainer bg="false" />

      {/* Add marginTop if your header is fixed */}
      <Section bgColor="--bs-fade-info" style={{ marginTop: "88px" }}>
        <Section.InnerContainer>
          <CenterWrap>
            <Section.Title>Our Property List</Section.Title>

            <Section.Content>
              {properties.length === 0 ? (
                <Empty>No properties found.</Empty>
              ) : (
                <Cards>
                  {properties.map((featured) => (
                    <ListingItemContainer
                      key={featured.id}
                      featured={featured}
                    />
                  ))}
                </Cards>
              )}
            </Section.Content>

            <Section.Footer style={{ textAlign: "center", marginTop: 18 }}>
              <Section.Button>More Listing</Section.Button>
            </Section.Footer>
          </CenterWrap>
        </Section.InnerContainer>
      </Section>

      <FooterContainer />
    </>
  );
};

export default Listing;
